import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { formatDistanceToNow } from 'date-fns';
import type { DashboardMetrics } from '@/types';

interface RecentClientsTableProps {
  clients: DashboardMetrics['recentClients'];
  isLoading?: boolean;
}

export function RecentClientsTable({ clients, isLoading }: RecentClientsTableProps) {
  if (isLoading) {
    return (
      <Card className="animate-pulse">
        <CardHeader>
          <div className="h-6 bg-muted rounded w-1/3"></div>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-16 bg-muted rounded"></div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  const getStatusVariant = (status: string) => {
    switch (status) {
      case 'in-progress':
        return 'default';
      case 'lead':
        return 'secondary';
      case 'completed':
        return 'outline';
      default:
        return 'secondary';
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'in-progress':
        return 'In Progress';
      case 'lead':
        return 'Lead';
      case 'completed':
        return 'Completed';
      default:
        return status;
    }
  };

  return (
    <Card className="shadow-sm" data-testid="card-recent-clients">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle>Recent Clients</CardTitle>
          <Button variant="ghost" size="sm" data-testid="button-view-all-clients">
