        {/* Bottom Actions */}
        <div className="mt-8 pt-4 border-t border-border space-y-2">
          <Button
            variant="ghost"
            className="w-full justify-start text-muted-foreground hover:text-foreground"
            data-testid="button-settings"
          >
            <Settings className="w-5 h-5 mr-3" />
